-- Name: find_platform_resource_by_cloud_id(character varying); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.find_platform_resource_by_cloud_id(p_cloud_resource_id character varying) RETURNS TABLE(workspace_id character varying, workspace_name character varying, platform_resource_id integer, platform_resource_name character varying, terraform_address text, resource_type character varying, cloud_resource_name character varying, module_path text, jump_url text)
    LANGUAGE plpgsql
    AS $$
BEGIN
    RETURN QUERY
    SELECT 
        ri.workspace_id,
        w.name::VARCHAR(100) as workspace_name,
        wr.id::INTEGER as platform_resource_id,
        wr.resource_name::VARCHAR(100) as platform_resource_name,
        ri.terraform_address,
        ri.resource_type,
        ri.cloud_resource_name,
        ri.module_path,
        CASE 
            WHEN wr.id IS NOT NULL THEN 
                CONCAT('/workspaces/', ri.workspace_id, '/resources/', wr.id)
            ELSE NULL
        END as jump_url
    FROM resource_index ri
    LEFT JOIN workspace_resources wr ON 
        ri.workspace_id = wr.workspace_id 
        AND ri.root_module_name = wr.resource_name
        AND wr.is_active = true
    LEFT JOIN workspaces w ON ri.workspace_id = w.workspace_id
    WHERE ri.cloud_resource_id = p_cloud_resource_id
    AND ri.resource_mode = 'managed';
END;
$$;


--
-- Name: generate_workspace_semantic_id(); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.generate_workspace_semantic_id() RETURNS character varying
    LANGUAGE plpgsql
    AS $$
DECLARE
    chars TEXT := 'abcdefghijklmnopqrstuvwxyz0123456789';
    result TEXT := 'ws-';
    i INTEGER;
    new_id TEXT;
    id_exists BOOLEAN;
BEGIN
    LOOP
        result := 'ws-';
        FOR i IN 1..16 LOOP
            result := result || substr(chars, floor(random() * length(chars) + 1)::integer, 1);
        END LOOP;
        
        -- 检查是否已存在
        SELECT EXISTS(SELECT 1 FROM workspaces WHERE workspace_id = result) INTO id_exists;
        
        IF NOT id_exists THEN
            RETURN result;
        END IF;
    END LOOP;
END;
$$;


--
-- Name: get_workspace_resource_tree(character varying); Type: FUNCTION; Schema: public; Owner: -
--

CREATE FUNCTION public.get_workspace_resource_tree(p_workspace_id character varying) RETURNS TABLE(node_type character varying, node_name character varying, node_path text, terraform_address text, resource_type character varying, cloud_resource_id character varying, cloud_resource_name character varying, description text, depth integer, parent_path text, resource_count integer)
    LANGUAGE plpgsql
    AS $$
BEGIN
    -- 返回module节点
    RETURN QUERY
    SELECT 
        'module'::VARCHAR(20) as node_type,
        mh.module_name::VARCHAR(255) as node_name,
        mh.module_path as node_path,
        NULL::TEXT as terraform_address,
        NULL::VARCHAR(100) as resource_type,
        NULL::VARCHAR(255) as cloud_resource_id,
        NULL::VARCHAR(255) as cloud_resource_name,
        NULL::TEXT as description,
        mh.depth,
        mh.parent_path,
        mh.total_resource_count as resource_count
    FROM module_hierarchy mh
    WHERE mh.workspace_id = p_workspace_id
    
    UNION ALL
    
    -- 返回资源节点
    SELECT 
        'resource'::VARCHAR(20) as node_type,
        ri.resource_name::VARCHAR(255) as node_name,
        ri.module_path as node_path,
        ri.terraform_address,
        ri.resource_type,
        ri.cloud_resource_id,
        ri.cloud_resource_name,
        ri.description,
        ri.module_depth + 1 as depth,
        ri.module_path as parent_path,
        1 as resource_count
    FROM resource_index ri
    WHERE ri.workspace_id = p_workspace_id
    
    ORDER BY depth, node_path, node_type DESC, node_name;
END;
$$;


