    resource_type,
    count(*) AS count,
    count(DISTINCT cloud_resource_id) AS unique_cloud_ids
   FROM public.resource_index
  WHERE ((resource_mode)::text = 'managed'::text)
  GROUP BY workspace_id, resource_type
  ORDER BY workspace_id, (count(*)) DESC;


