CREATE VIEW public.v_user_effective_roles AS
 SELECT ur.user_id,
    ur.role_id,
    ur.scope_type,
    ur.scope_id,
    r.name AS role_name,
    r.display_name AS role_display_name,
    ur.assigned_at,
    ur.expires_at
   FROM (public.iam_user_roles ur
     JOIN public.iam_roles r ON ((ur.role_id = r.id)))
