 SELECT workspace_id,
    count(*) AS total_resources,
    count(DISTINCT resource_type) AS resource_type_count,
    count(DISTINCT root_module_name) AS root_module_count,
    count(*) FILTER (WHERE ((resource_mode)::text = 'managed'::text)) AS managed_count,
    count(*) FILTER (WHERE ((resource_mode)::text = 'data'::text)) AS data_count,
    max(last_synced_at) AS last_synced_at
   FROM public.resource_index
  GROUP BY workspace_id;


